import React from 'react';
import { Grid, Image, Header, Container, Form, TextArea, Button } from 'semantic-ui-react';
import './estandar.css';
import { BrowserRouter as Router, Switch, Route, Link, useRouteMatch, useParams, useHistory } from 'react-router-dom';

const Perfil = () => {
	

	const history = useHistory();
	const [id, setId] = useState('')
    const [nombre, setNombre] = useState('');
    const [apellido, setApellido] = useState('')
	const [correo, setCorreo] = useState('')
	const [carrera, setCarrera] = useState('')
	const [telefono, setTelefono] = useState('')

    const handleId = (e) => {
        setId(e);
    }
    
    const handleNombre = (e) => {
        setNombre(e);
    }
    
    const handleApellido = (e) => {
        setApellido(e);
    }
    
    const handleCorreo = (e) => {
        setCorreo(e);
    }
    
    const handleCarrera = (e) => {
        setCarrera(e);
    }

    const handleTelefono = (e) => {
        setTelefono(e);
    }
	
	const getData = () => {

        axios.get("http://localhost:8080/ruta/perfil",
           {
            }).then(res => {
                const data = res.data;
                handleNombre(data.nombre);
                handleApellido(data.apellido);
                handleEmail(data.email);
                handleCiudad(data.ciudad);
                handleDireccion(data.direccion);
                handleGenero(data.genero);
                handleEdad(data.edad);
            });
    }

	
	//if (user != usuario_admitido[0] && password != usuario_admitido[1]) {
	//	history.push('/');
	//}

	return (
		<Grid style={{ marginLeft: '450px', marginTop: '100px' }}>
			<Grid.Row>
				<Grid.Column width={4}>
					<Image src="https://react.semantic-ui.com/images/avatar/large/matthew.png" />
				</Grid.Column>
				<Grid.Column width={8}>
					<Container text>
						<Header style={{ marginBottom: '0px' }} as="h1">
							Lucas Bernard<Header disabled as="h4">
								#1234
							</Header>
						</Header>
						<Header style={{ marginTop: '0px' }} as="h4" disabled>
							Lucas.bernard@usm.cl
						</Header>
						<Header as="h3">Ingenería Civil Informática</Header>
						<Link to="/Ccontraseña">Cambiar contraseña</Link>
						<Header as="h3">Intereses: </Header>
						<p>
							Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo ligula eget dolor.
							Aenean massa strong. Cum sociis natoque penatibus et magnis dis parturient montes, nascetur
							ridiculus mus. Donec quam felis, ultricies nec, pellentesque eu, pretium quis, sem. Nulla
							consequat massa quis enim.
						</p>
					</Container>
				</Grid.Column>
			</Grid.Row>
			<Grid.Row>
				<Grid.Column width={12}>
					<Form>
						<TextArea placeholder="Descripcion" />
					</Form>
				</Grid.Column>
				<Button color="teal">Agregar Descripción</Button>
			</Grid.Row>
		</Grid>
	);
};

export default Perfil;
