import React, { useState } from 'react';
import { Grid, Image, Header, Container, Form, TextArea, Button, Table, Tab } from 'semantic-ui-react';
import './estandar.css';
import { BrowserRouter as Link, useHistory } from 'react-router-dom';
import axios from 'axios';

const Sala = () => {
	let session_correo = localStorage.getItem('session_correo');
	let session_agregar = localStorage.getItem('session_agregar');
	console.log(session_agregar, 'es la session agregar');

	const history = useHistory();
	const [ showState, setShowState ] = React.useState(true);
	const [ ramo, setRamo ] = useState('');
	const [ nombre, setNombre ] = useState('');
	const [ grupo, setGrupo ] = useState([]);
	const [ valor, setValor ] = useState('');
	const [ integrantes, setIntegrantes ] = useState([]);

	const handleRamo = (e) => {
		setRamo(e);
	};

	const handleNombre = (e) => {
		setNombre(e);
	};

	const handleGrupo = (e) => {
		setGrupo(e);
	};
	const handleAgregar = (e) => {
		e.preventDefault();
		setValor(e.target.value);

		history.push('/AgregarAGrupo');
	};
	const handleIntegrantes = (e) => {
		setIntegrantes(e);
	};

	console.log(session_agregar);

	const getData = () => {
		axios
			.get('http://localhost:8000/rutasGrupo/Sala', {
				headers: { _id: session_agregar }
			})
			.then((data) => {
				handleIntegrantes(data.data.integrantes);
			});
	};
	if (showState === true) {
		getData();
		setShowState(false);
	}

	//if (user != usuario_admitido[0] && password != usuario_admitido[1]) {
	//	history.push('/');
	//}
	return (
		<Grid divided="vertically " style={{ marginLeft: 300, marginTop: 100, marginRight: 100 }}>
			<Grid.Row columns={3}>
				<Grid.Column>
					<Table celled>
						<Table.Header>
							<Table.Row>
								<Table.HeaderCell>Integrantes</Table.HeaderCell>
							</Table.Row>
						</Table.Header>
						<Table.Body>
							{integrantes.map((integrante) => {
								return (
									<Table.Row>
										<Table.Cell>{integrante}</Table.Cell>
									</Table.Row>
								);
							})}
						</Table.Body>
					</Table>
				</Grid.Column>
				<Grid.Column>Pizarra</Grid.Column>
				<Grid.Column>
					<Table.Row>Opciones</Table.Row>
					<Table.Row>
						<Button>Agregar usuarios al grupo</Button>
					</Table.Row>
				</Grid.Column>
			</Grid.Row>
		</Grid>
	);
};

export default Sala;
